function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c);b.center()});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var a=b.find('textarea[name="message"]');var c=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var h=$.trim(a.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var d=$.trim(c.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var f=Bitcoin.Message.verifyMessage(d,h);b.find(".address-result-txt").text(f);b.find(".address-result").show(200)}catch(g){makeNotice("error","misc-error","Error Verifying Message"+g);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var d=addresses[a];if(!d||!d.priv){c.modal("hide");return}var h=$.trim(b.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var g=decodePK(d.priv);var f=new Bitcoin.ECKey(g);var e=Bitcoin.Message.signMessage(f,h,d.addr);c.find(".signature").show(200);c.find(".signature-result").text(e)})})}function showLabelAddressModal(a){var c=$("#label-address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(a);var b=c.find('input[name="label"]');c.find(".address").text(a);b.val("");c.find(".btn.btn-primary").unbind().click(function(){c.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[a].label=d;backupWalletDelayed();buildVisibleView()});c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")})};