function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c);b.center()});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var a=b.find('textarea[name="message"]');var c=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var g=$.trim(a.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(c.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var d=Bitcoin.Message.verifyMessage(h,g);b.find(".address-result-txt").text(d);b.find(".address-result").show(200)}catch(f){makeNotice("error","misc-error","Error Verifying Message"+f);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var d=addresses[a];if(!d||!d.priv){b.modal("hide");return}var e=$.trim(c.val());if(!e||e.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var g=decodePK(d.priv);var h=new Bitcoin.ECKey(g);var f=Bitcoin.Message.signMessage(h,e,d.addr);b.find(".signature").show(200);b.find(".signature-result").text(f)})})}function showLabelAddressModal(b){var c=$("#label-address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(b);var a=c.find('input[name="label"]');c.find(".address").text(b);a.val("");c.find(".btn.btn-primary").unbind().click(function(){c.modal("hide");var d=a.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[b].label=d;backupWalletDelayed();buildVisibleView()});c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")})};