function showAddressModalMenu(c){var a=addresses[c];if(a==null){makeNotice("error","misc-error","Address Not Found");return}var b=$("#address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(c);if(a.tag==2){b.find(".address-archive-button").hide()}if(a.priv==null){b.find(".address-sign-message").hide()}b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find(".address-label-button").unbind().click(function(){b.modal("hide");showLabelAddressModal(c)});b.find(".address-qr-code-button").unbind().click(function(){b.modal("hide");showAddressModalQRCode(c)});b.find(".address-sign-message").unbind().click(function(){b.modal("hide");showAddressModalSignMessage(c)});b.find(".address-archive-button").unbind().click(function(){b.modal("hide");archiveAddr(c)})}function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c)});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var a=b.find('textarea[name="message"]');var c=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var g=$.trim(a.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(c.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(g+" "+h);var f=Bitcoin.Message.verifyMessage(h,g);b.find(".address-result-txt").text(f);b.find(".address-result").show(200)}catch(d){makeNotice("error","misc-error","Error Verifying Message"+d);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var d=addresses[a];if(!d||!d.priv){b.modal("hide");return}var e=$.trim(c.val());if(!e||e.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var h=decodePK(d.priv);var g=new Bitcoin.ECKey(h);var f=Bitcoin.Message.signMessage(g,e,d.addr);b.find(".signature").show(200);b.find(".signature-result").text(f)})})}function showLabelAddressModal(a){var b=$("#label-address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);var c=b.find('input[name="label"]');b.find(".address").text(a);c.val("");b.find(".btn.btn-primary").unbind().click(function(){b.modal("hide");var d=c.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[a].label=d;backupWalletDelayed();buildVisibleView()});b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})};