function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c);b.center()});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var a=$("#verify-message-modal");a.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");a.find(".address-result").hide();var c=a.find('textarea[name="message"]');var b=a.find('textarea[name="signature"]');a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")});a.find("textarea").bind("change",function(){a.find(".address-result").hide()});a.find(".btn.btn-primary").unbind().click(function(){try{var g=$.trim(c.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var d=Bitcoin.Message.verifyMessage(f,g);a.find(".address-result-txt").text(d);a.find(".address-result").show(200)}catch(h){makeNotice("error","misc-error","Error Verifying Message"+h);a.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var h=addresses[a];if(!h||!h.priv){c.modal("hide");return}var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var g=decodePK(h.priv);var e=new Bitcoin.ECKey(g);var d=Bitcoin.Message.signMessage(e,f,h.addr);c.find(".signature").show(200);c.find(".signature-result").text(d)})})}function showLabelAddressModal(c){var b=$("#label-address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(c);var a=b.find('input[name="label"]');b.find(".address").text(c);a.val("");b.find(".btn.btn-primary").unbind().click(function(){b.modal("hide");var d=a.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})};