function showAddressModalMenu(b){var c=addresses[b];if(c==null){makeNotice("error","misc-error","Address Not Found");return}var a=$("#address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);if(c.tag==2){a.find(".address-archive-button").hide()}if(c.priv==null){a.find(".address-sign-message").hide()}a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")});a.find(".address-label-button").unbind().click(function(){a.modal("hide");labelAddressModal(b)});a.find(".address-qr-code-button").unbind().click(function(){a.modal("hide");showAddressModalQRCode(b)});a.find(".address-sign-message").unbind().click(function(){a.modal("hide");showAddressModalSignMessage(b)});a.find(".address-archive-button").unbind().click(function(){a.modal("hide");archiveAddr(b)})}function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c)});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var c=$("#verify-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");c.find(".address-result").hide();var b=c.find('textarea[name="message"]');var a=c.find('textarea[name="signature"]');c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find("textarea").bind("change",function(){c.find(".address-result").hide()});c.find(".btn.btn-primary").unbind().click(function(){try{var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var d=$.trim(a.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(f+" "+d);var h=Bitcoin.Message.verifyMessage(d,f);c.find(".address-result-txt").text(h);c.find(".address-result").show(200)}catch(g){makeNotice("error","misc-error","Error Verifying Message"+g);c.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var h=addresses[a];if(!h||!h.priv){c.modal("hide");return}var g=$.trim(b.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var e=decodePK(h.priv);var f=new Bitcoin.ECKey(e);var d=Bitcoin.Message.signMessage(f,g,h.addr);c.find(".signature").show(200);c.find(".signature-result").text(d)})})}function labelAddressModal(c){var a=$("#label-address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(c);var b=a.find('input[name="label"]');a.find(".address").text(c);b.val("");a.find(".btn.btn-primary").unbind().click(function(){a.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})};