function showAddressModalMenu(a){var c=addresses[a];if(c==null){makeNotice("error","misc-error","Address Not Found");return}var b=$("#address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);if(c.tag==2){b.find(".address-archive-button").hide()}if(c.priv==null){b.find(".address-sign-message").hide()}b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find(".address-label-button").unbind().click(function(){b.modal("hide");labelAddressModal(a)});b.find(".address-qr-code-button").unbind().click(function(){b.modal("hide");showAddressModalQRCode(a)});b.find(".address-sign-message").unbind().click(function(){b.modal("hide");showAddressModalSignMessage(a)});b.find(".address-archive-button").unbind().click(function(){b.modal("hide");archiveAddr(a)})}function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c)});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var c=$("#verify-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");c.find(".address-result").hide();var a=c.find('textarea[name="message"]');var b=c.find('textarea[name="signature"]');c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find("textarea").bind("change keydown",function(){c.find(".address-result").hide()});c.find(".btn.btn-primary").unbind().click(function(){try{var g=$.trim(a.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(g+" "+f);var d=Bitcoin.Message.verifyMessage(f,g);c.find(".address-result-txt").text(d);c.find(".address-result").show(200)}catch(h){makeNotice("error","misc-error","Error Verifying Message"+h);c.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change keydown",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var h=addresses[a];if(!h||!h.priv){c.modal("hide");return}var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var g=decodePK(h.priv);var e=new Bitcoin.ECKey(g);var d=Bitcoin.Message.signMessage(e,f,true);c.find(".signature").show(200);c.find(".signature-result").text(d)})})}function labelAddressModal(c){var a=$("#label-address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(c);var b=a.find('input[name="label"]');a.find(".address").text(c);b.val("");a.find(".btn.btn-primary").unbind().click(function(){a.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})};