function showAddressModalMenu(c){var a=addresses[c];if(a==null){makeNotice("error","misc-error","Address Not Found");return}var b=$("#address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(c);if(a.tag==2){b.find(".address-archive-button").hide()}if(a.priv==null){b.find(".address-sign-message").hide()}b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find(".address-label-button").unbind().click(function(){b.modal("hide");labelAddressModal(c)});b.find(".address-qr-code-button").unbind().click(function(){b.modal("hide");showAddressModalQRCode(c)});b.find(".address-sign-message").unbind().click(function(){b.modal("hide");showAddressModalSignMessage(c)});b.find(".address-archive-button").unbind().click(function(){b.modal("hide");archiveAddr(c)})}function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c)});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var c=b.find('textarea[name="message"]');var a=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var f=$.trim(c.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(a.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(f+" "+h);var g=Bitcoin.Message.verifyMessage(h,f);b.find(".address-result-txt").text(g);b.find(".address-result").show(200)}catch(d){makeNotice("error","misc-error","Error Verifying Message"+d);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var d=addresses[a];if(!d||!d.priv){c.modal("hide");return}var e=$.trim(b.val());if(!e||e.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var h=decodePK(d.priv);var f=new Bitcoin.ECKey(h);var g=Bitcoin.Message.signMessage(f,e,d.addr);c.find(".signature").show(200);c.find(".signature-result").text(g)})})}function labelAddressModal(a){var c=$("#label-address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(a);var b=c.find('input[name="label"]');c.find(".address").text(a);b.val("");c.find(".btn.btn-primary").unbind().click(function(){c.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[a].label=d;backupWalletDelayed();buildVisibleView()});c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")})};