function showAddressModalMenu(b){var a=addresses[b];if(a==null){makeNotice("error","misc-error","Address Not Found");return}var c=$("#address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(b);if(a.tag==2){c.find(".address-archive-button").hide()}if(a.priv==null){c.find(".address-sign-message").hide()}c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find(".address-label-button").unbind().click(function(){c.modal("hide");labelAddressModal(b)});c.find(".address-qr-code-button").unbind().click(function(){c.modal("hide");showAddressModalQRCode(b)});c.find(".address-sign-message").unbind().click(function(){c.modal("hide");showAddressModalSignMessage(b)});c.find(".address-archive-button").unbind().click(function(){c.modal("hide");archiveAddr(b)})}function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c)});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var a=b.find('textarea[name="message"]');var c=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var g=$.trim(a.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var d=$.trim(c.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(g+" "+d);var f=Bitcoin.Message.verifyMessage(d,g);b.find(".address-result-txt").text(f);b.find(".address-result").show(200)}catch(h){makeNotice("error","misc-error","Error Verifying Message"+h);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var e=addresses[a];if(!e||!e.priv){b.modal("hide");return}var h=$.trim(c.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var f=decodePK(e.priv);var g=new Bitcoin.ECKey(f);var d=Bitcoin.Message.signMessage(g,h,e.addr);b.find(".signature").show(200);b.find(".signature-result").text(d)})})}function labelAddressModal(a){var c=$("#label-address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(a);var b=c.find('input[name="label"]');c.find(".address").text(a);b.val("");c.find(".btn.btn-primary").unbind().click(function(){c.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[a].label=d;backupWalletDelayed();buildVisibleView()});c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")})};