function showAddressModalMenu(b){var a=addresses[b];if(a==null){makeNotice("error","misc-error","Address Not Found");return}var c=$("#address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(b);if(a.tag==2){c.find(".address-archive-button").hide()}if(a.priv==null){c.find(".address-sign-message").hide()}c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find(".address-label-button").unbind().click(function(){c.modal("hide");labelAddressModal(b)});c.find(".address-qr-code-button").unbind().click(function(){c.modal("hide");showAddressModalQRCode(b)});c.find(".address-sign-message").unbind().click(function(){c.modal("hide");showAddressModalSignMessage(b)});c.find(".address-archive-button").unbind().click(function(){c.modal("hide");archiveAddr(b)})}function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c)});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var c=$("#verify-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");c.find(".address-result").hide();var b=c.find('textarea[name="message"]');var a=c.find('textarea[name="signature"]');c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find("textarea").bind("change",function(){c.find(".address-result").hide()});c.find(".btn.btn-primary").unbind().click(function(){try{var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(a.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(f+" "+h);var g=Bitcoin.Message.verifyMessage(h,f);c.find(".address-result-txt").text(g);c.find(".address-result").show(200)}catch(d){makeNotice("error","misc-error","Error Verifying Message"+d);c.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var e=addresses[a];if(!e||!e.priv){c.modal("hide");return}var d=$.trim(b.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var h=decodePK(e.priv);var f=new Bitcoin.ECKey(h);var g=Bitcoin.Message.signMessage(f,d,e.addr);c.find(".signature").show(200);c.find(".signature-result").text(g)})})}function labelAddressModal(a){var c=$("#label-address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(a);var b=c.find('input[name="label"]');c.find(".address").text(a);b.val("");c.find(".btn.btn-primary").unbind().click(function(){c.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[a].label=d;backupWalletDelayed();buildVisibleView()});c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")})};