function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c);b.center()});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var c=$("#verify-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");c.find(".address-result").hide();var b=c.find('textarea[name="message"]');var a=c.find('textarea[name="signature"]');c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find("textarea").bind("change",function(){c.find(".address-result").hide()});c.find(".btn.btn-primary").unbind().click(function(){try{var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var g=$.trim(a.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var h=Bitcoin.Message.verifyMessage(g,f);c.find(".address-result-txt").text(h);c.find(".address-result").show(200)}catch(d){makeNotice("error","misc-error","Error Verifying Message"+d);c.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var g=addresses[a];if(!g||!g.priv){c.modal("hide");return}var d=$.trim(b.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var f=decodePK(g.priv);var h=new Bitcoin.ECKey(f);var e=Bitcoin.Message.signMessage(h,d,g.addr);c.find(".signature").show(200);c.find(".signature-result").text(e)})})}function showLabelAddressModal(a){var b=$("#label-address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);var c=b.find('input[name="label"]');b.find(".address").text(a);c.val("");b.find(".btn.btn-primary").unbind().click(function(){b.modal("hide");var d=c.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[a].label=d;backupWalletDelayed();buildVisibleView()});b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})};