function showAddressModalMenu(b){var c=addresses[b];if(c==null){makeNotice("error","misc-error","Address Not Found");return}var a=$("#address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);if(c.tag==2){a.find(".address-archive-button").hide()}if(c.priv==null){a.find(".address-sign-message").hide()}a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")});a.find(".address-label-button").unbind().click(function(){a.modal("hide");showLabelAddressModal(b)});a.find(".address-qr-code-button").unbind().click(function(){a.modal("hide");showAddressModalQRCode(b)});a.find(".address-sign-message").unbind().click(function(){a.modal("hide");showAddressModalSignMessage(b)});a.find(".address-archive-button").unbind().click(function(){a.modal("hide");archiveAddr(b)})}function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c)});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var a=b.find('textarea[name="message"]');var c=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var f=$.trim(a.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(c.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(f+" "+h);var g=Bitcoin.Message.verifyMessage(h,f);b.find(".address-result-txt").text(g);b.find(".address-result").show(200)}catch(d){makeNotice("error","misc-error","Error Verifying Message"+d);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var h=addresses[a];if(!h||!h.priv){b.modal("hide");return}var e=$.trim(c.val());if(!e||e.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var g=decodePK(h.priv);var d=new Bitcoin.ECKey(g);var f=Bitcoin.Message.signMessage(d,e,h.addr);b.find(".signature").show(200);b.find(".signature-result").text(f)})})}function showLabelAddressModal(c){var b=$("#label-address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(c);var a=b.find('input[name="label"]');b.find(".address").text(c);a.val("");b.find(".btn.btn-primary").unbind().click(function(){b.modal("hide");var d=a.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})};