function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c);a.center()});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var c=$("#verify-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");c.find(".address-result").hide();var a=c.find('textarea[name="message"]');var b=c.find('textarea[name="signature"]');c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find("textarea").bind("change",function(){c.find(".address-result").hide()});c.find(".btn.btn-primary").unbind().click(function(){try{var d=$.trim(a.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var h=Bitcoin.Message.verifyMessage(f,d);c.find(".address-result-txt").text(h);c.find(".address-result").show(200)}catch(g){makeNotice("error","misc-error","Error Verifying Message"+g);c.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var h=addresses[a];if(!h||!h.priv){b.modal("hide");return}var d=$.trim(c.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var g=decodePK(h.priv);var e=new Bitcoin.ECKey(g);var f=Bitcoin.Message.signMessage(e,d,h.addr);b.find(".signature").show(200);b.find(".signature-result").text(f)})})}function showLabelAddressModal(c){var b=$("#label-address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(c);var a=b.find('input[name="label"]');b.find(".address").text(c);a.val("");b.find(".btn.btn-primary").unbind().click(function(){b.modal("hide");var d=a.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})};