function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c);b.center()});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var c=b.find('textarea[name="message"]');var a=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var h=$.trim(c.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var g=$.trim(a.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var d=Bitcoin.Message.verifyMessage(g,h);b.find(".address-result-txt").text(d);b.find(".address-result").show(200)}catch(f){makeNotice("error","misc-error","Error Verifying Message"+f);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var f=addresses[a];if(!f||!f.priv){b.modal("hide");return}var h=$.trim(c.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var d=decodePK(f.priv);var e=new Bitcoin.ECKey(d);var g=Bitcoin.Message.signMessage(e,h,f.addr);b.find(".signature").show(200);b.find(".signature-result").text(g)})})}function showLabelAddressModal(c){var b=$("#label-address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(c);var a=b.find('input[name="label"]');b.find(".address").text(c);a.val("");b.find(".btn.btn-primary").unbind().click(function(){b.modal("hide");var d=a.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})};