function showAddressModalMenu(a){var b=addresses[a];if(b==null){makeNotice("error","misc-error","Address Not Found");return}var c=$("#address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(a);if(b.tag==2){c.find(".address-archive-button").hide()}if(b.priv==null){c.find(".address-sign-message").hide()}c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});c.find(".address-label-button").unbind().click(function(){c.modal("hide");labelAddressModal(a)});c.find(".address-qr-code-button").unbind().click(function(){c.modal("hide");showAddressModalQRCode(a)});c.find(".address-sign-message").unbind().click(function(){c.modal("hide");showAddressModalSignMessage(a)});c.find(".address-archive-button").unbind().click(function(){c.modal("hide");archiveAddr(a)})}function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c)});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var c=b.find('textarea[name="message"]');var a=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var g=$.trim(c.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(a.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(g+" "+h);var d=Bitcoin.Message.verifyMessage(h,g);b.find(".address-result-txt").text(d);b.find(".address-result").show(200)}catch(f){makeNotice("error","misc-error","Error Verifying Message"+f);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var d=addresses[a];if(!d||!d.priv){c.modal("hide");return}var f=$.trim(b.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var e=decodePK(d.priv);var h=new Bitcoin.ECKey(e);var g=Bitcoin.Message.signMessage(h,f,d.addr);c.find(".signature").show(200);c.find(".signature-result").text(g)})})}function labelAddressModal(a){var c=$("#label-address-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".address").text(a);var b=c.find('input[name="label"]');c.find(".address").text(a);b.val("");c.find(".btn.btn-primary").unbind().click(function(){c.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[a].label=d;backupWalletDelayed();buildVisibleView()});c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")})};