function showAddressModalMenu(a){var c=addresses[a];if(c==null){makeNotice("error","misc-error","Address Not Found");return}var b=$("#address-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);if(c.tag==2){b.find(".address-archive-button").hide()}if(c.priv==null){b.find(".address-sign-message").hide()}b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find(".address-label-button").unbind().click(function(){b.modal("hide");labelAddressModal(a)});b.find(".address-qr-code-button").unbind().click(function(){b.modal("hide");showAddressModalQRCode(a)});b.find(".address-sign-message").unbind().click(function(){b.modal("hide");showAddressModalSignMessage(a)});b.find(".address-archive-button").unbind().click(function(){b.modal("hide");archiveAddr(a)})}function showAddressModalQRCode(a){var b=$("#qr-code-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".address").text(a);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,a);b.find(".address-qr-code").empty().append(c)});b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")})}function verifyMessageModal(){var a=$("#verify-message-modal");a.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");a.find(".address-result").hide();var c=a.find('textarea[name="message"]');var b=a.find('textarea[name="signature"]');a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")});a.find("textarea").bind("change",function(){a.find(".address-result").hide()});a.find(".btn.btn-primary").unbind().click(function(){try{var f=$.trim(c.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var g=$.trim(b.val());if(!g||g.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}console.log(f+" "+g);var h=Bitcoin.Message.verifyMessage(g,f);a.find(".address-result-txt").text(h);a.find(".address-result").show(200)}catch(d){makeNotice("error","misc-error","Error Verifying Message"+d);a.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var h=addresses[a];if(!h||!h.priv){b.modal("hide");return}var d=$.trim(c.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var g=decodePK(h.priv);var e=new Bitcoin.ECKey(g);var f=Bitcoin.Message.signMessage(e,d,h.addr);b.find(".signature").show(200);b.find(".signature-result").text(f)})})}function labelAddressModal(c){var a=$("#label-address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(c);var b=a.find('input[name="label"]');a.find(".address").text(c);b.val("");a.find(".btn.btn-primary").unbind().click(function(){a.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})};