$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(l){var g=$.parseJSON(l.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;try{playSound("beep")}catch(l){console.log(l)}var d=TransactionFromJSON(op);d.setConfirmations(0);var k=0;for(var j=0;j<d.inputs.length;j++){var f=d.inputs[j];console.log(f.prev_out.addr);if(f.prev_out.addr==address){k-=parseInt(f.prev_out.value)}}console.log("result "+k);var h=0;for(var j=0;j<d.out.length;j++){var c=d.out[j];if(c.addr==address){h+=parseInt(c.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+h);k+=h;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k);flashTitle("New Transaction");d.result=k;$("#tx_container").prepend(d.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});