$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(f){var g=$.parseJSON(f.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;try{playSound("beep")}catch(f){console.log(f)}var j=TransactionFromJSON(op);j.setConfirmations(0);var d=0;for(var c=0;c<j.inputs.length;c++){var l=j.inputs[c];console.log(l.prev_out.addr);if(l.prev_out.addr==address){d-=parseInt(l.prev_out.value)}}console.log("result "+d);var k=0;for(var c=0;c<j.out.length;c++){var h=j.out[c];if(h.addr==address){k+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+k);d+=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");j.result=d;$("#tx_container").prepend(j.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});