$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(l){var g=$.parseJSON(l.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;try{playSound("beep")}catch(l){console.log(l)}var f=TransactionFromJSON(op);f.setConfirmations(0);var c=0;for(var j=0;j<f.inputs.length;j++){var d=f.inputs[j];console.log(d.prev_out.addr);if(d.prev_out.addr==address){c-=parseInt(d.prev_out.value)}}console.log("result "+c);var k=0;for(var j=0;j<f.out.length;j++){var h=f.out[j];if(h.addr==address){k+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+k);c+=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+c);flashTitle("New Transaction");f.result=c;$("#no_tx").hide();$("#tx_container").prepend(f.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});