$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(f){var k=$.parseJSON(f.data);if(k.op=="status"){$("#status").html(k.msg)}else{if(k.op=="utx"){op=k.x;try{playSound("beep")}catch(f){console.log(f)}var j=TransactionFromJSON(op);j.setConfirmations(0);var h=0;for(var d=0;d<j.inputs.length;d++){var c=j.inputs[d];console.log(c.prev_out.addr);if(c.prev_out.addr==address){h-=parseInt(c.prev_out.value)}}console.log("result "+h);var g=0;for(var d=0;d<j.out.length;d++){var l=j.out[d];if(l.addr==address){g+=parseInt(l.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+g);h+=g;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+h);flashTitle("New Transaction");j.result=h;$("#no_tx").hide();$("#tx_container").prepend(j.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});