$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(f){var l=$.parseJSON(f.data);if(l.op=="status"){$("#status").html(l.msg)}else{if(l.op=="utx"){op=l.x;try{playSound("beep")}catch(f){console.log(f)}var h=TransactionFromJSON(op);h.setConfirmations(0);var d=0;for(var g=0;g<h.inputs.length;g++){var k=h.inputs[g];console.log(k.prev_out.addr);if(k.prev_out.addr==address){d-=parseInt(k.prev_out.value)}}console.log("result "+d);var c=0;for(var g=0;g<h.out.length;g++){var j=h.out[g];if(j.addr==address){c+=parseInt(j.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);d+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");h.result=d;$("#tx_container").prepend(h.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});