$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(j){var h=$.parseJSON(j.data);if(h.op=="status"){$("#status").html(h.msg)}else{if(h.op=="utx"){op=h.x;try{playSound("beep")}catch(j){console.log(j)}var g=TransactionFromJSON(op);g.setConfirmations(0);var f=0;for(var d=0;d<g.inputs.length;d++){var k=g.inputs[d];console.log(k.prev_out.addr);if(k.prev_out.addr==address){f-=parseInt(k.prev_out.value)}}console.log("result "+f);var l=0;for(var d=0;d<g.out.length;d++){var c=g.out[d];if(c.addr==address){l+=parseInt(c.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+l);f+=l;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+f);flashTitle("New Transaction");g.result=f;$("#tx_container").prepend(g.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});