$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(h){var f=$.parseJSON(h.data);if(f.op=="status"){$("#status").html(f.msg)}else{if(f.op=="utx"){op=f.x;try{playSound("beep")}catch(h){console.log(h)}var g=TransactionFromJSON(op);g.setConfirmations(0);var l=0;for(var k=0;k<g.inputs.length;k++){var d=g.inputs[k];console.log(d.prev_out.addr);if(d.prev_out.addr==address){l-=parseInt(d.prev_out.value)}}console.log("result "+l);var c=0;for(var k=0;k<g.out.length;k++){var j=g.out[k];if(j.addr==address){c+=parseInt(j.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);l+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+l);flashTitle("New Transaction");g.result=l;$("#tx_container").prepend(g.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});