$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(h){var d=$.parseJSON(h.data);if(d.op=="status"){$("#status").html(d.msg)}else{if(d.op=="utx"){op=d.x;try{playSound("beep")}catch(h){console.log(h)}var k=TransactionFromJSON(op);k.setConfirmations(0);var f=0;for(var c=0;c<k.inputs.length;c++){var g=k.inputs[c];console.log(g.prev_out.addr);if(g.prev_out.addr==address){f-=parseInt(g.prev_out.value)}}console.log("result "+f);var j=0;for(var c=0;c<k.out.length;c++){var l=k.out[c];if(l.addr==address){j+=parseInt(l.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+j);f+=j;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+f);flashTitle("New Transaction");k.result=f;$("#tx_container").prepend(k.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});