$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet("${address}")});$("#deposit").click(function(){loadScript("${wallet_resource}deposit/deposit.js",function(){showDepositModal("${address}","sms","Instant Deposit By Phone / SMS")})});$("#payment-request").click(function(){loadScript("${wallet_resource}payment-request.js",function(){showPaymentRequestModal("${address}","Payment Request")})});var b=makeQRCode(255,250,1,"${address}");$("#qr-code").append(b);$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket("ws://api.blockchain.info:8335/inv");a.onmessage=function(m){var l=$.parseJSON(m.data);if(l.op=="status"){$("#status").html(l.msg)}else{if(l.op=="utx"){op=l.x;try{playSound("beep")}catch(m){console.log(m)}var f=TransactionFromJSON(op);f.setConfirmations(0);var d=0;for(var j=0;j<f.inputs.length;j++){var h=f.inputs[j];if(h.prev_out.addr=="${address}"){var k=parseInt(h.prev_out.value);d-=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k)}}for(var j=0;j<f.out.length;j++){var g=f.out[j];if(g.addr!="${address}"){var k=parseInt(g.value);d+=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k);$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+k)}}flashTitle("New Transaction");f.result=d;$("#tx_container").prepend(f.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"${address}"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(c){console.log(c)}});