$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(l){var k=$.parseJSON(l.data);if(k.op=="status"){$("#status").html(k.msg)}else{if(k.op=="utx"){op=k.x;try{playSound("beep")}catch(l){console.log(l)}var c=TransactionFromJSON(op);c.setConfirmations(0);var d=0;for(var h=0;h<c.inputs.length;h++){var f=c.inputs[h];console.log(f.prev_out.addr);if(f.prev_out.addr==address){d-=parseInt(f.prev_out.value)}}console.log("result "+d);var g=0;for(var h=0;h<c.out.length;h++){var j=c.out[h];if(j.addr==address){g+=parseInt(j.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+g);d+=g;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");c.result=d;$("#no_tx").hide();$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});