$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(k){var j=$.parseJSON(k.data);if(j.op=="status"){$("#status").html(j.msg)}else{if(j.op=="utx"){op=j.x;try{playSound("beep")}catch(k){console.log(k)}var g=TransactionFromJSON(op);g.setConfirmations(0);var l=0;for(var c=0;c<g.inputs.length;c++){var d=g.inputs[c];console.log(d.prev_out.addr);if(d.prev_out.addr==address){l-=parseInt(d.prev_out.value)}}console.log("result "+l);var f=0;for(var c=0;c<g.out.length;c++){var h=g.out[c];if(h.addr==address){f+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+f);l+=f;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+l);flashTitle("New Transaction");g.result=l;$("#no_tx").hide();$("#tx_container").prepend(g.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});