$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(f){var d=$.parseJSON(f.data);if(d.op=="status"){$("#status").html(d.msg)}else{if(d.op=="utx"){op=d.x;try{playSound("beep")}catch(f){console.log(f)}var l=TransactionFromJSON(op);l.setConfirmations(0);var k=0;for(var j=0;j<l.inputs.length;j++){var c=l.inputs[j];console.log(c.prev_out.addr);if(c.prev_out.addr==address){k-=parseInt(c.prev_out.value)}}console.log("result "+k);var h=0;for(var j=0;j<l.out.length;j++){var g=l.out[j];if(g.addr==address){h+=parseInt(g.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+h);k+=h;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k);flashTitle("New Transaction");l.result=k;$("#tx_container").prepend(l.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});