$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(l){var f=$.parseJSON(l.data);if(f.op=="status"){$("#status").html(f.msg)}else{if(f.op=="utx"){op=f.x;try{playSound("beep")}catch(l){console.log(l)}var d=TransactionFromJSON(op);d.setConfirmations(0);var c=0;for(var j=0;j<d.inputs.length;j++){var g=d.inputs[j];console.log(g.prev_out.addr);if(g.prev_out.addr==address){c-=parseInt(g.prev_out.value)}}console.log("result "+c);var h=0;for(var j=0;j<d.out.length;j++){var k=d.out[j];if(k.addr==address){h+=parseInt(k.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+h);c+=h;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+c);flashTitle("New Transaction");d.result=c;$("#tx_container").prepend(d.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});