$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(d){var c=$.parseJSON(d.data);if(c.op=="status"){$("#status").html(c.msg)}else{if(c.op=="utx"){op=c.x;try{playSound("beep")}catch(d){console.log(d)}var f=TransactionFromJSON(op);f.setConfirmations(0);var j=0;for(var l=0;l<f.inputs.length;l++){var g=f.inputs[l];console.log(g.prev_out.addr);if(g.prev_out.addr==address){j-=parseInt(g.prev_out.value)}}console.log("result "+j);var k=0;for(var l=0;l<f.out.length;l++){var h=f.out[l];if(h.addr==address){k+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+k);j+=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+j);flashTitle("New Transaction");f.result=j;$("#tx_container").prepend(f.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});