$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(h){var d=$.parseJSON(h.data);if(d.op=="status"){$("#status").html(d.msg)}else{if(d.op=="utx"){op=d.x;try{playSound("beep")}catch(h){console.log(h)}var k=TransactionFromJSON(op);k.setConfirmations(0);var g=0;for(var f=0;f<k.inputs.length;f++){var j=k.inputs[f];console.log(j.prev_out.addr);if(j.prev_out.addr==address){g-=parseInt(j.prev_out.value)}}console.log("result "+g);var c=0;for(var f=0;f<k.out.length;f++){var l=k.out[f];if(l.addr==address){c+=parseInt(l.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);g+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+g);flashTitle("New Transaction");k.result=g;$("#no_tx").hide();$("#tx_container").prepend(k.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});