$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(l){var f=$.parseJSON(l.data);if(f.op=="status"){$("#status").html(f.msg)}else{if(f.op=="utx"){op=f.x;try{playSound("beep")}catch(l){console.log(l)}var k=TransactionFromJSON(op);k.setConfirmations(0);var g=0;for(var d=0;d<k.inputs.length;d++){var j=k.inputs[d];console.log(j.prev_out.addr);if(j.prev_out.addr==address){g-=parseInt(j.prev_out.value)}}console.log("result "+g);var c=0;for(var d=0;d<k.out.length;d++){var h=k.out[d];if(h.addr==address){c+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);g+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+g);flashTitle("New Transaction");k.result=g;$("#tx_container").prepend(k.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});