$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(j){var k=$.parseJSON(j.data);if(k.op=="status"){$("#status").html(k.msg)}else{if(k.op=="utx"){op=k.x;try{playSound("beep")}catch(j){console.log(j)}var c=TransactionFromJSON(op);c.setConfirmations(0);var f=0;for(var g=0;g<c.inputs.length;g++){var l=c.inputs[g];console.log(l.prev_out.addr);if(l.prev_out.addr==address){f-=parseInt(l.prev_out.value)}}console.log("result "+f);var d=0;for(var g=0;g<c.out.length;g++){var h=c.out[g];if(h.addr==address){d+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+d);f+=d;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+f);flashTitle("New Transaction");c.result=f;$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});