$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(d){var l=$.parseJSON(d.data);if(l.op=="status"){$("#status").html(l.msg)}else{if(l.op=="utx"){op=l.x;try{playSound("beep")}catch(d){console.log(d)}var c=TransactionFromJSON(op);c.setConfirmations(0);var k=0;for(var h=0;h<c.inputs.length;h++){var f=c.inputs[h];console.log(f.prev_out.addr);if(f.prev_out.addr==address){k-=parseInt(f.prev_out.value)}}console.log("result "+k);var j=0;for(var h=0;h<c.out.length;h++){var g=c.out[h];if(g.addr==address){j+=parseInt(g.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+j);k+=j;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k);flashTitle("New Transaction");c.result=k;$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});