$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(g){var c=$.parseJSON(g.data);if(c.op=="status"){$("#status").html(c.msg)}else{if(c.op=="utx"){op=c.x;try{playSound("beep")}catch(g){console.log(g)}var h=TransactionFromJSON(op);h.setConfirmations(0);var k=0;for(var l=0;l<h.inputs.length;l++){var j=h.inputs[l];console.log(j.prev_out.addr);if(j.prev_out.addr==address){k-=parseInt(j.prev_out.value)}}console.log("result "+k);var f=0;for(var l=0;l<h.out.length;l++){var d=h.out[l];if(d.addr==address){f+=parseInt(d.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+f);k+=f;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k);flashTitle("New Transaction");h.result=k;$("#tx_container").prepend(h.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});