$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(d){var k=$.parseJSON(d.data);if(k.op=="status"){$("#status").html(k.msg)}else{if(k.op=="utx"){op=k.x;try{playSound("beep")}catch(d){console.log(d)}var j=TransactionFromJSON(op);j.setConfirmations(0);var g=0;for(var h=0;h<j.inputs.length;h++){var f=j.inputs[h];console.log(f.prev_out.addr);if(f.prev_out.addr==address){g-=parseInt(f.prev_out.value)}}console.log("result "+g);var c=0;for(var h=0;h<j.out.length;h++){var l=j.out[h];if(l.addr==address){c+=parseInt(l.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);g+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+g);flashTitle("New Transaction");j.result=g;$("#tx_container").prepend(j.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});