$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(d){var k=$.parseJSON(d.data);if(k.op=="status"){$("#status").html(k.msg)}else{if(k.op=="utx"){op=k.x;try{playSound("beep")}catch(d){console.log(d)}var j=TransactionFromJSON(op);j.setConfirmations(0);var c=0;for(var f=0;f<j.inputs.length;f++){var l=j.inputs[f];console.log(l.prev_out.addr);if(l.prev_out.addr==address){c-=parseInt(l.prev_out.value)}}console.log("result "+c);var g=0;for(var f=0;f<j.out.length;f++){var h=j.out[f];if(h.addr==address){g+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+g);c+=g;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+c);flashTitle("New Transaction");j.result=c;$("#tx_container").prepend(j.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});