$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(k){var l=$.parseJSON(k.data);if(l.op=="status"){$("#status").html(l.msg)}else{if(l.op=="utx"){op=l.x;try{playSound("beep")}catch(k){console.log(k)}var g=TransactionFromJSON(op);g.setConfirmations(0);var h=0;for(var f=0;f<g.inputs.length;f++){var j=g.inputs[f];console.log(j.prev_out.addr);if(j.prev_out.addr==address){h-=parseInt(j.prev_out.value)}}console.log("result "+h);var d=0;for(var f=0;f<g.out.length;f++){var c=g.out[f];if(c.addr==address){d+=parseInt(c.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+d);h+=d;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+h);flashTitle("New Transaction");g.result=h;$("#tx_container").prepend(g.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});