$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(f){var j=$.parseJSON(f.data);if(j.op=="status"){$("#status").html(j.msg)}else{if(j.op=="utx"){op=j.x;try{playSound("beep")}catch(f){console.log(f)}var h=TransactionFromJSON(op);h.setConfirmations(0);var d=0;for(var l=0;l<h.inputs.length;l++){var c=h.inputs[l];console.log(c.prev_out.addr);if(c.prev_out.addr==address){d-=parseInt(c.prev_out.value)}}console.log("result "+d);var k=0;for(var l=0;l<h.out.length;l++){var g=h.out[l];if(g.addr==address){k+=parseInt(g.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+k);d+=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");h.result=d;$("#tx_container").prepend(h.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});