$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(h){var k=$.parseJSON(h.data);if(k.op=="status"){$("#status").html(k.msg)}else{if(k.op=="utx"){op=k.x;try{playSound("beep")}catch(h){console.log(h)}var c=TransactionFromJSON(op);c.setConfirmations(0);var g=0;for(var j=0;j<c.inputs.length;j++){var l=c.inputs[j];console.log(l.prev_out.addr);if(l.prev_out.addr==address){g-=parseInt(l.prev_out.value)}}console.log("result "+g);var f=0;for(var j=0;j<c.out.length;j++){var d=c.out[j];if(d.addr==address){f+=parseInt(d.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+f);g+=f;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+g);flashTitle("New Transaction");c.result=g;$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});