$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(k){var g=$.parseJSON(k.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;try{playSound("beep")}catch(k){console.log(k)}var c=TransactionFromJSON(op);c.setConfirmations(0);var d=0;for(var h=0;h<c.inputs.length;h++){var l=c.inputs[h];console.log(l.prev_out.addr);if(l.prev_out.addr==address){d-=parseInt(l.prev_out.value)}}console.log("result "+d);var f=0;for(var h=0;h<c.out.length;h++){var j=c.out[h];if(j.addr==address){f+=parseInt(j.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+f);d+=f;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");c.result=d;$("#no_tx").hide();$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});