$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(h){var g=$.parseJSON(h.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;try{playSound("beep")}catch(h){console.log(h)}var l=TransactionFromJSON(op);l.setConfirmations(0);var c=0;for(var d=0;d<l.inputs.length;d++){var f=l.inputs[d];console.log(f.prev_out.addr);if(f.prev_out.addr==address){c-=parseInt(f.prev_out.value)}}console.log("result "+c);var j=0;for(var d=0;d<l.out.length;d++){var k=l.out[d];if(k.addr==address){j+=parseInt(k.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+j);c+=j;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+c);flashTitle("New Transaction");l.result=c;$("#no_tx").hide();$("#tx_container").prepend(l.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});