$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(d){var g=$.parseJSON(d.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;try{playSound("beep")}catch(d){console.log(d)}var c=TransactionFromJSON(op);c.setConfirmations(0);var j=0;for(var f=0;f<c.inputs.length;f++){var h=c.inputs[f];console.log(h.prev_out.addr);if(h.prev_out.addr==address){j-=parseInt(h.prev_out.value)}}console.log("result "+j);var l=0;for(var f=0;f<c.out.length;f++){var k=c.out[f];if(k.addr==address){l+=parseInt(k.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+l);j+=l;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+j);flashTitle("New Transaction");c.result=j;$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});