$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(h){var f=$.parseJSON(h.data);if(f.op=="status"){$("#status").html(f.msg)}else{if(f.op=="utx"){op=f.x;try{playSound("beep")}catch(h){console.log(h)}var d=TransactionFromJSON(op);d.setConfirmations(0);var l=0;for(var k=0;k<d.inputs.length;k++){var c=d.inputs[k];console.log(c.prev_out.addr);if(c.prev_out.addr==address){l-=parseInt(c.prev_out.value)}}console.log("result "+l);var j=0;for(var k=0;k<d.out.length;k++){var g=d.out[k];if(g.addr==address){j+=parseInt(g.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+j);l+=j;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+l);flashTitle("New Transaction");d.result=l;$("#no_tx").hide();$("#tx_container").prepend(d.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});