$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(c){var l=$.parseJSON(c.data);if(l.op=="status"){$("#status").html(l.msg)}else{if(l.op=="utx"){op=l.x;try{playSound("beep")}catch(c){console.log(c)}var h=TransactionFromJSON(op);h.setConfirmations(0);var g=0;for(var k=0;k<h.inputs.length;k++){var d=h.inputs[k];console.log(d.prev_out.addr);if(d.prev_out.addr==address){g-=parseInt(d.prev_out.value)}}console.log("result "+g);var f=0;for(var k=0;k<h.out.length;k++){var j=h.out[k];if(j.addr==address){f+=parseInt(j.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+f);g+=f;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+g);flashTitle("New Transaction");h.result=g;$("#tx_container").prepend(h.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});