$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(l){var j=$.parseJSON(l.data);if(j.op=="status"){$("#status").html(j.msg)}else{if(j.op=="utx"){op=j.x;try{playSound("beep")}catch(l){console.log(l)}var c=TransactionFromJSON(op);c.setConfirmations(0);var f=0;for(var h=0;h<c.inputs.length;h++){var g=c.inputs[h];console.log(g.prev_out.addr);if(g.prev_out.addr==address){f-=parseInt(g.prev_out.value)}}console.log("result "+f);var k=0;for(var h=0;h<c.out.length;h++){var d=c.out[h];if(d.addr==address){k+=parseInt(d.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+k);f+=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+f);flashTitle("New Transaction");c.result=f;$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});