$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(g){var k=$.parseJSON(g.data);if(k.op=="status"){$("#status").html(k.msg)}else{if(k.op=="utx"){op=k.x;try{playSound("beep")}catch(g){console.log(g)}var j=TransactionFromJSON(op);j.setConfirmations(0);var f=0;for(var l=0;l<j.inputs.length;l++){var d=j.inputs[l];console.log(d.prev_out.addr);if(d.prev_out.addr==address){f-=parseInt(d.prev_out.value)}}console.log("result "+f);var c=0;for(var l=0;l<j.out.length;l++){var h=j.out[l];if(h.addr==address){c+=parseInt(h.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);f+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+f);flashTitle("New Transaction");j.result=f;$("#no_tx").hide();$("#tx_container").prepend(j.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});