$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(j){var h=$.parseJSON(j.data);if(h.op=="status"){$("#status").html(h.msg)}else{if(h.op=="utx"){op=h.x;try{playSound("beep")}catch(j){console.log(j)}var l=TransactionFromJSON(op);l.setConfirmations(0);var d=0;for(var c=0;c<l.inputs.length;c++){var g=l.inputs[c];console.log(g.prev_out.addr);if(g.prev_out.addr==address){d-=parseInt(g.prev_out.value)}}console.log("result "+d);var k=0;for(var c=0;c<l.out.length;c++){var f=l.out[c];if(f.addr==address){k+=parseInt(f.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+k);d+=k;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");l.result=d;$("#tx_container").prepend(l.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});