$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(l){var g=$.parseJSON(l.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;try{playSound("beep")}catch(l){console.log(l)}var j=TransactionFromJSON(op);j.setConfirmations(0);var d=0;for(var k=0;k<j.inputs.length;k++){var h=j.inputs[k];console.log(h.prev_out.addr);if(h.prev_out.addr==address){d-=parseInt(h.prev_out.value)}}console.log("result "+d);var c=0;for(var k=0;k<j.out.length;k++){var f=j.out[k];if(f.addr==address){c+=parseInt(f.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);d+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");j.result=d;$("#tx_container").prepend(j.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});