$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(c){var l=$.parseJSON(c.data);if(l.op=="status"){$("#status").html(l.msg)}else{if(l.op=="utx"){op=l.x;try{playSound("beep")}catch(c){console.log(c)}var g=TransactionFromJSON(op);g.setConfirmations(0);var f=0;for(var j=0;j<g.inputs.length;j++){var k=g.inputs[j];console.log(k.prev_out.addr);if(k.prev_out.addr==address){f-=parseInt(k.prev_out.value)}}console.log("result "+f);var h=0;for(var j=0;j<g.out.length;j++){var d=g.out[j];if(d.addr==address){h+=parseInt(d.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+h);f+=h;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+f);flashTitle("New Transaction");g.result=f;$("#tx_container").prepend(g.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});