$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(l){var h=$.parseJSON(l.data);if(h.op=="status"){$("#status").html(h.msg)}else{if(h.op=="utx"){op=h.x;try{playSound("beep")}catch(l){console.log(l)}var g=TransactionFromJSON(op);g.setConfirmations(0);var j=0;for(var f=0;f<g.inputs.length;f++){var d=g.inputs[f];console.log(d.prev_out.addr);if(d.prev_out.addr==address){j-=parseInt(d.prev_out.value)}}console.log("result "+j);var c=0;for(var f=0;f<g.out.length;f++){var k=g.out[f];if(k.addr==address){c+=parseInt(k.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);j+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+j);flashTitle("New Transaction");g.result=j;$("#tx_container").prepend(g.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});